import React from 'react';
import {Header, Button, Modal, Dimmer, ModalHeader} from 'semantic-ui-react';
import axios from 'axios';
import baseUrl from '../../utils/baseUrl';
import {useRouter} from 'next/router';

function MatchAttributes({opponent, _id}) {
  const [modal, setModal] = React.useState(false);
  const router = useRouter();
  async function handleDelete(){

    const url = `${baseUrl}/api/Match`;
    const payload = { params: {_id }};
    await axios.delete(url, payload);
    router.push('/');
  }

  return <>
    <Header as="h3">About this Match</Header>
    <p>{opponent}</p>
    <Button 
      icon="trash alternate outline"
      color="red"
      content="Delete Match"
      onClick={() => setModal(true)}
    />

    <Modal open={modal} dimmer="blurring">
      <Modal.Header>Confirm Delete</Modal.Header>
      <Modal.Content>
        <p>Are you sure you want to delete this Match?</p>
      </Modal.Content>
      <Modal.Actions>
        <Button 
        onClick={() => setModal(false)}
        content="Cancel"/>
        <Button 
          negative
          icon="trash"
          labelPosition="right"
          content="Delete"
          onClick={handleDelete}
        />
      </Modal.Actions>

    </Modal>
  </>;
}

export default MatchAttributes;
